"use strict";

$(function () {
  !function () {
    var e = $(".inner"),
        n = $(".imgList").find(".imgItem"),
        i = n.height(),
        t = (n.length, 0);
    e.css({
      height: i + "px"
    }), n.hide(), n.eq(t).show();
    var l = "onwheel" in document ? "wheel" : "onmousewheel" in document ? "mousewheel" : "DOMMouseScroll";
    e.on(l, function (e) {
      e.preventDefault();
      e.originalEvent.deltaY ? e.originalEvent.deltaY : e.originalEvent.wheelDelta ? e.originalEvent.wheelDelta : e.originalEvent.detail;
    });
  }();
});